import React, {Component} from 'react';
import Word from './Word';
import DisplayPoem from './DisplayPoem';

class AcrosticContainer extends Component {

  state = {
    word: '',
    letters: []
  }

  handleChange = event => {
    this.setState({
      word: event
    })
  }

  render() {
    return (
      <>
        <Word />
        <DisplayPoem />
      </>
    );
  }
}

export default AcrosticContainer;
