  }}

  getWords = () => {
      console.log(this.props.wordData.letters)
      this.props.wordData.letters.map(letter => {
        return <Word letter={letter} />
      })
  }

  render() {
    return (
        <div>
          <h1>YOURPOEMHERE</h1>
          {this.getWords()}
